(17, 1, '2021-03-04 15:48:12', '2021-03-04 15:48:12', '<!-- wp:gallery {\"ids\":[18,19,20],\"columns\":2,\"linkTo\":\"none\",\"align\":\"left\"} -->\n<figure class=\"wp-block-gallery alignleft columns-2 is-cropped\"><ul class=\"blocks-gallery-grid\"><li class=\"blocks-gallery-item\"><figure><img src=\"http://localhost/myweb/wp-content/uploads/2021/03/dine.jpg\" alt=\"\" data-id=\"18\" data-full-url=\"http://localhost/myweb/wp-content/uploads/2021/03/dine.jpg\" data-link=\"http://localhost/myweb/?attachment_id=18\" class=\"wp-image-18\"/></figure></li><li class=\"blocks-gallery-item\"><figure><img src=\"http://localhost/myweb/wp-content/uploads/2021/03/dine2.jpg\" alt=\"\" data-id=\"19\" data-full-url=\"http://localhost/myweb/wp-content/uploads/2021/03/dine2.jpg\" data-link=\"http://localhost/myweb/?attachment_id=19\" class=\"wp-image-19\"/></figure></li><li class=\"blocks-gallery-item\"><figure><img src=\"http://localhost/myweb/wp-content/uploads/2021/03/room.jpg\" alt=\"\" data-id=\"20\" data-full-url=\"http://localhost/myweb/wp-content/uploads/2021/03/room.jpg\" data-link=\"http://localhost/myweb/?attachment_id=20\" class=\"wp-image-20\"/></figure></li></ul></figure>\n<!-- /wp:gallery -->\n\n<!-- wp:heading {\"level\":1,\"backgroundColor\":\"white\"} -->\n<h1 class=\"has-white-background-color has-background\">Karachi\'s most secure hotel, an oasis in the city</h1>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"center\",\"className\":\"reward\"} -->\n<p class=\"has-text-align-center reward\"><em>&nbsp;The  Hotel TM is located in the heart of the city, opposite Frere Hall and Park. The hotel enjoys excellent accessibility to and from all directions. It offers an array of services, including an executive level with private lounge, 24-hour business center, 24-hour room service, health club, with outdoor swimming pool and spa. The most popular Clifton Beach and Dolman Shopping Mall are situated just ten minutes drive</em></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"textAlign\":\"center\"} -->\n<h2 class=\"has-text-align-center\">Highlights</h2>\n<!-- /wp:heading -->\n\n<!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:paragraph -->\n<p>Location - City Centre, accessibility and convenient. 18km away from the airport. Most secure hotel in the city.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:paragraph -->\n<p>Experienced, Professional team that offers flexibility and warm hospitality.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:paragraph -->\n<p>State of the art technology for meetings and functions. High-Speed wireless internet Access</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:heading {\"textAlign\":\"center\"} -->\n<h2 class=\"has-text-align-center\">Guest Rooms</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">All rooms include free breakfast &amp; free Wi -Fi</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:columns {\"backgroundColor\":\"white\",\"textColor\":\"black\"} -->\n<div class=\"wp-block-columns has-black-color has-white-background-color has-text-color has-background\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:image {\"id\":34,\"sizeSlug\":\"large\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-large\"><img src=\"http://localhost/myweb/wp-content/uploads/2021/03/khipk-guestroom-0053-hor-clsc-1.jpg\" alt=\"\" class=\"wp-image-34\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4>Deluxe King</h4>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Guest room, 1 King</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:buttons -->\n<div class=\"wp-block-buttons\"><!-- wp:button {\"borderRadius\":0,\"textColor\":\"black\",\"gradient\":\"luminous-vivid-amber-to-luminous-vivid-orange\"} -->\n<div class=\"wp-block-button\"><a class=\"wp-block-button__link has-black-color has-luminous-vivid-amber-to-luminous-vivid-orange-gradient-background has-text-color has-background no-border-radius\">CHECK RATES</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:buttons --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:image {\"id\":29,\"sizeSlug\":\"large\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-large\"><img src=\"http://localhost/myweb/wp-content/uploads/2021/03/khipk-guestroom-0053-hor-clsc.jpg\" alt=\"\" class=\"wp-image-29\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4>Deluxe Twin</h4>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"style\":{\"typography\":{\"fontSize\":15}}} -->\n<p style=\"font-size:15px\">Guest room, 2 Twin</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:buttons -->\n<div class=\"wp-block-buttons\"><!-- wp:button {\"borderRadius\":0,\"textColor\":\"black\",\"gradient\":\"luminous-vivid-amber-to-luminous-vivid-orange\"} -->\n<div class=\"wp-block-button\"><a class=\"wp-block-button__link has-black-color has-luminous-vivid-amber-to-luminous-vivid-orange-gradient-background has-text-color has-background no-border-radius\">CHECK RATES</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:buttons --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:image {\"id\":30,\"sizeSlug\":\"large\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-large\"><img src=\"http://localhost/myweb/wp-content/uploads/2021/03/khipk-twin-1780-hor-clsc.jpg\" alt=\"\" class=\"wp-image-30\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:heading {\"level\":5} -->\n<h5><strong>Twin/Single Bed(s)</strong></h5>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Executive lounge&nbsp;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:buttons -->\n<div class=\"wp-block-buttons\"><!-- wp:button {\"borderRadius\":0,\"te